mapper.insert:

insert into goods (id, type_id, name, 
  subtitle, main_image, sub_images, 
  price, stock, status, 
  create_time, update_time, detail
  )
values (#{id,jdbcType=INTEGER}, #{typeId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{subtitle,jdbcType=VARCHAR}, #{mainImage,jdbcType=VARCHAR}, #{subImages,jdbcType=VARCHAR}, 
  #{price,jdbcType=DECIMAL}, #{stock,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{detail,jdbcType=LONGVARCHAR}
  )

mapper.select:
select id, type_id, name, subtitle, main_image, sub_images, price, stock, status,
create_time, update_time, detail
from neuedu_product


    id=#{goodsId}


    and name like #{goodsName}

select id, type_id, name, subtitle, main_image, sub_images, price, stock, pstatus,
create_time, update_time, detail
from goods


    and type_id IN
    /*循环开始前加(   结束加) 每循环一次加一次,  */

      #{type_id}



    and name like #{keyword}
